mapper.update:
UPDATE t_serve
c_title=#{title}, c_image=#{image}, c_work_type=#{workType}, c_pay_type=#{payType}, c_price=#{price}, c_price_type=#{priceType}, c_validity=#{validity}, c_serve_time=#{serveTime}, c_menu_id=#{menuId}, c_links=#{links}, c_province_name=#{provinceName}, c_description=#{description}, c_citycode=#{citycode}, c_longitude=#{longitude}, c_latitude=#{latitude}, c_city_name=#{cityName}, c_area=#{area}, c_detail_address=#{detailAddress}, c_keywords=#{keywords}, c_published=#{published}
WHERE pk_id = #{id}

mapper.select:
		 SELECT t.*,
		 m.c_name as menuName,
		 me.c_name as memberName

		 ,ROUND(
		 	6378.138*2*ASIN(SQRT(POW(SIN((#{latitude}*PI()/180-c_latitude*PI()/180)/2),2)
		 	+COS(#{latitude}*PI()/180)*COS(c_latitude*PI()/180)*POW(SIN((#{longitude}*PI()/180-c_longitude*PI()/180)/2),2)))*1000)
		 AS
 		 c_distance

		 FROM t_serve t,
		 t_menu m,
		 t_member me 
		 WHERE 
		 m.pk_id = t.c_menu_id 
		 and me.pk_id = t.c_member_id
		 and t.c_type = #{type}

		 and me.c_age <= #{maxAge}
		 and me.c_age >= #{minAge}

		 and t.c_latitude >= #{minLatitude}
		 and t.c_latitude <= #{maxLatitude}
		 and t.c_longitude >= #{minLongitude}
		 and t.c_longitude >= #{maxLongitude}

		 and me.c_skillScore <= #{maxSkill}
		 and me.c_skillScore >= #{minSkill}

		 and me.c_creditScore <= #{maxCredit}
		 and me.c_creditScore >= #{minCredit}

		 and t.c_member_id = #{memberId}

		 and me.c_gender = #{gender}

		 and t.c_menu_id = #{menuId}

		 and t.c_citycode = #{citycode}



		  	and 
		  	(
				(str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",#{startTime}),"%Y-%m-%d %H:%i:%s") > str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",SUBSTRING_INDEX(t.c_serve_time,"-",1)),'%Y-%m-%d %H:%i')
				and 
				str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",#{startTime}),"%Y-%m-%d %H:%i:%s") < str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",SUBSTRING_INDEX(t.c_serve_time,"-",-1)),'%Y-%m-%d %H:%i')
				)
				or
				(str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",#{endTime}),"%Y-%m-%d %H:%i:%s") > str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",SUBSTRING_INDEX(t.c_serve_time,"-",1)),'%Y-%m-%d %H:%i')
				and 
				str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",#{endTime}),"%Y-%m-%d %H:%i:%s") < str_to_date(concat(DATE_FORMAT(now(),'%Y-%m-%d')," ",SUBSTRING_INDEX(t.c_serve_time,"-",-1)),'%Y-%m-%d %H:%i')
				)
			)
